mapper.update:
update admin
set last_login_time = #{lastLoginTime}
where id = #{id} and status = 1

update admin
set status = #{status}
where id = #{id}

mapper.select:
select a.id, `name`, password, avatar, create_time, last_login_time, status
from admin a , admin_role ar
where a.id = ar.admin_id and ar.role_id = #{roleId}
