mapper.insert:
insert into blogroll (bid, bname, burl
  )
values (#{bid,jdbcType=INTEGER}, #{bname,jdbcType=VARCHAR}, #{burl,jdbcType=VARCHAR}
  )

mapper.update:
update blogroll
set bname = #{bname,jdbcType=VARCHAR},
  burl = #{burl,jdbcType=VARCHAR}
where bid = #{bid,jdbcType=INTEGER}

mapper.select:
select bid, bname, burl
from blogroll
where bid = #{bid,jdbcType=INTEGER}
