mapper.update:
update menu
set text = #{text,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  parentid = #{parentid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, text, url, parentid
from menu
where id = #{id,jdbcType=INTEGER}

select id, text, url, parentid
from menu
where parentid = #{id}
