mapper.update:
update user
set uname = #{uname,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR}
where uid = #{uid,jdbcType=INTEGER}

mapper.select:
select uid, uname, email, password,headimg
from user
where uid = #{uid,jdbcType=INTEGER}

select uid, uname, email, password,headimg
from user
where email = #{emailAddress}
